import React from 'react';
import Row from 'react-bootstrap/Row';
import Sixteen from '../atom.jpeg';
import Fifteen from '../visualstudio.jpeg';
import Fourteen from '../phpstorm.jpeg';
import Thirteen from '../github.png';
import Twelve from '../windows.jpeg';
import Eleven from '../linux.jpeg';
import Ten from '../sql.png';
import Nine from '../postgresql.png';
import Eight from '../laravel.png';
import Seven from '../java.png';
import Six from '../php1.jpeg';
import Five from '../css.png';
import Four from '../html.png';
import Three from '../bootstrap.jpeg';
import Two from '../vue.png';
import One from '../react.png';
import Col from 'react-bootstrap/Col';
import DeveloperImg from '../12.jpg';
import Fade from 'react-reveal/Fade';
import Bounce from 'react-reveal/Bounce';
import $ from 'jquery';

export default function Language() {
    $(document).ready(function(){
        animateDiv('.aa');
         animateDiv('.ab');
          animateDiv('.ac'); 
          animateDiv('.ad');
           animateDiv('.ae');
            animateDiv('.af');
             animateDiv('.ag');
              animateDiv('.ah');
               animateDiv('.ai');
                animateDiv('.aj'); 
                animateDiv('.ak'); 
                animateDiv('.al'); 
                animateDiv('.am');
                animateDiv('.an');
                animateDiv('.ao');
                animateDiv('.ap');

    });

    function makeNewPosition(){

        // Get viewport dimensions (remove the dimension of the div)
        var h = $(window).height() - 100;
        var w = $(window).width() - 100;

        var nh = Math.floor(Math.random() * h);
        var nw = Math.floor(Math.random() * w);

        return [nh,nw];    

    }

    function animateDiv(myclass){
        var newq = makeNewPosition();
        $(myclass).animate({ top: newq[0], left: newq[1] }, 3000,   function(){
          animateDiv(myclass);        
        });

    };
    return (
        <React.Fragment>
            {/* Heading  */}
             <Row className="main">
                <img className="theSmiley" src="https://www.kirupa.com/icon/1f385.svg" alt="languages" />
                <div className="item" >
                <img src={Sixteen} alt="languages"  style={{animationDelay: "-1.5s"}} /> 
                </div>
                <div>
                <img className="item"  src={Fifteen} alt="languages" style={{animationDelay: "-3s"}}/>
                </div>
                <div>
                <img className="item"  src={Fourteen} alt="languages" style={{animationDelay: "-4.5s"}}/>
                </div>
                <div>
                <img className="item" src={Thirteen} alt="languages" style={{animationDelay: "-6s"}} />
                </div>
                <div>
                <img className="item" src={Nine} alt="languages" style={{animationDelay: "-7.5s"}}/> 
                </div>
                <div>
                <img className="item" src={Ten} alt="languages" style={{animationDelay: "-9s"}}/>
                </div>
                <div>
                <img className="item" src={Eleven} alt="languages" style={{animationDelay: "-10.5s"}}/> 
                </div>
                <div>
                <img className="item" src={Twelve} alt="languages" style={{animationDelay: "-12s"}}/> 
                </div>
                <div>
                <img className="item" src={Six} alt="languages" style={{animationDelay: "-13.5s"}}/>
                </div>
                <div>
                <img className="item" src={Seven} alt="languages" style={{animationDelay: "-15s"}}/>
                </div>
                <div>
                <img className="item" src={Eight} alt="languages" style={{animationDelay: "-16.5s"}}/>
                </div>
                <div>
                <img className="item" src={One} alt="languages" style={{animationDelay: "-18s"}}/>
                </div>
                <div>
                <img className="item" src={Two} alt="languages" style={{animationDelay: "-19.5s"}}/>
                </div>
                <div>
                <img className="item" src={Three} alt="languages" style={{animationDelay: "-21s"}}/>
                </div>
                <div>
                <img className="item" src={Four} alt="languages" style={{animationDelay: "-22.5s"}}/>
                </div>
                <div>
                <img className="item" src={Five} alt="languages" style={{animationDelay: "-24s"}}/>
                </div>
            </Row>
            {/* details of language known */}
            <Row xs={1} md={2} lg={2} className="mt-5">
                <Fade left>
                    <Col>
                        <img src={DeveloperImg} style={{width:"100%"}} alt="developerImg"/>
                    </Col>
                </Fade>
                <Fade right>
                    <Col className="Exp languages">
                        <Row className="justify-content-center">
                            <Bounce top cascade>
                                <h4 className="mt-2">Languages Known</h4>
                            </Bounce>
                        </Row>
                        <Row className="justify-content-center">
                            <h5 className="mt-2">Frontend Languages</h5> 
                        </Row>
                        <Row className="justify-content-center">
                            <img src={One} alt="languages" />
                            <img src={Two} alt="languages" />
                            <img src={Three} alt="languages" />
                            <img src={Four} alt="languages" />
                            <img src={Five} alt="languages" />
                        </Row>
                        <Row className="justify-content-center">
                            <h5>Backend Languages</h5>
                        </Row>
                        <Row className="justify-content-center">
                            <img src={Six} alt="languages" />
                            <img src={Seven} alt="languages" />
                            <img src={Eight} alt="languages" />
                        </Row>
                        <Row className="justify-content-center">
                            <h5>Database & OS</h5>
                        </Row>
                        <Row className="justify-content-center">
                            <img src={Nine} alt="languages" /> 
                            <img src={Ten} alt="languages" />
                            <img src={Eleven} alt="languages" /> 
