import React from 'react';
import Row from 'react-bootstrap/Row';
import Sixteen from '../atom.jpeg';
import Fifteen from '../visualstudio.jpeg';
import Fourteen from '../phpstorm.jpeg';
import Thirteen from '../github.png';
import Twelve from '../windows.jpeg';
import Eleven from '../linux.jpeg';
import Ten from '../sql.png';
import Nine from '../postgresql.png';
import Eight from '../laravel.png';
import Seven from '../java.png';
import Six from '../php1.jpeg';
import Five from '../css.png';
import Four from '../html.png';
import Three from '../bootstrap.jpeg';
import Two from '../vue.png';
import One from '../react.png';
import Col from 'react-bootstrap/Col';
import DeveloperImg from '../12.jpg';
import Fade from 'react-reveal/Fade';
import Bounce from 'react-reveal/Bounce';
import $ from 'jquery';

export default function Language() {
    $(document).ready(function(){
        animateDiv('.a');
        animateDiv('.b');
        animateDiv('.c');
        animateDiv('.d');
    });

    function makeNewPosition(){

        // Get viewport dimensions (remove the dimension of the div)
        var h = $(window).height() - 50;
        var w = $(window).width() - 50;

        var nh = Math.floor(Math.random() * h);
        var nw = Math.floor(Math.random() * w);

        return [nh,nw];    

    }

    function animateDiv(myclass){
        var newq = makeNewPosition();
        $(myclass).animate({ top: newq[0], left: newq[1] }, 1000,   function(){
          animateDiv(myclass);        
        });

    };
    return (
        <React.Fragment>
            {/* Heading  */}
             <Row className="mt-5 justify-content-center align-items-center">
             <div class='a'></div>
<div class='b'></div>
<div class='c'></div>
<div class='d'></div>
            </Row>
            {/* details of language known */}
            <Row xs={1} md={2} lg={2} className="mt-5">
                <Fade left>
                    <Col>
                        <img src={DeveloperImg} style={{width:"100%"}} alt="developerImg"/>
                    </Col>
                </Fade>
                <Fade right>
                    <Col className="Exp languages">
                        <Row className="justify-content-center">
                            <Bounce top cascade>
                                <h4 className="mt-2">Languages Known</h4>
                            </Bounce>
                        </Row>
                        <Row className="justify-content-center">
                            <h5 className="mt-2">Frontend Languages</h5> 
                        </Row>
                        <Row className="justify-content-center">
                            <img src={One} alt="languages" />
                            <img src={Two} alt="languages" />
                            <img src={Three} alt="languages" />
                            <img src={Four} alt="languages" />
                            <img src={Five} alt="languages" />
                        </Row>
                        <Row className="justify-content-center">
                            <h5>Backend Languages</h5>
                        </Row>
                        <Row className="justify-content-center">
                            <img src={Six} alt="languages" />
                            <img src={Seven} alt="languages" />
                            <img src={Eight} alt="languages" />
                        </Row>
                        <Row className="justify-content-center">
                            <h5>Database & OS</h5>
                        </Row>
                        <Row className="justify-content-center">
                            <img src={Nine} alt="languages" /> 
                            <img src={Ten} alt="languages" />
                            <img src={Eleven} alt="languages" /> 
                            <img src={Twelve} alt="languages" />  
                        </Row>
                        <Row className="justify-content-center">
                            <h5>Tools & Version Control</h5> 
                        </Row>
                        <Row className="justify-content-center">
                            <img src={Sixteen} alt="languages" /> 
                            <img src={Fifteen} alt="languages" />
                            <img src={Fourteen} alt="languages" />
                            <img src={Thirteen} alt="languages" />   
                        </Row>
                    </Col>
                </Fade>
            </Row>
        </React.Fragment>
    )
}
