import React, {useState} from 'react';
import Row from 'react-bootstrap/Row';
import Ten from '../sql.png';
import Nine from '../linux.jpeg';
import Eight from '../laravel.png';
import Seven from '../java.png';
import Six from '../php1.jpeg';
import Five from '../css.png';
import Four from '../html.png';
import Three from '../bootstrap.jpeg';
import Two from '../vue.png';
import One from '../react.png';
import Col from 'react-bootstrap/Col';
import DeveloperImg from '../12.jpg';
import Zoom from 'react-reveal/Zoom';
import Fade from 'react-reveal/Fade';
import Spin from 'react-reveal/Spin';
import Bounce from 'react-reveal/Bounce';
import Jump from 'react-reveal/Jump';
import Wobble from 'react-reveal/Wobble';

export default function Language() {
    const [index, setIndex] = useState(0);
    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    };
    return (
        <React.Fragment>
            {/* Heading  */}
             <Row className="mt-5 justify-content-center align-items-center">
                <Bounce top cascade>
                    <h2>Languages Known</h2>
                </Bounce>
            </Row>
            {/* details of language known */}
            <Row xs={1} md={2} lg={2} className="mt-5">
                <Fade left>
                    <Col>
                        <img src={DeveloperImg} style={{width:"100%"}}/>
                    </Col>
                </Fade>
                <Fade right>
                    <Col className="Exp languages">
                        <Row className="justify-content-center">
                            <h5 className="mt-2">Frontend Languages</h5> 
                        </Row>
                        <Row className="justify-content-center">
                            <img src={One} alt="languages" />
                            <img src={Two} alt="languages" />
                            <img src={Three} alt="languages" />
                            <img src={Four} alt="languages" />
                            <img src={Five} alt="languages" />
                        </Row>
                        <Row className="justify-content-center">
                            <h5>Backend Languages</h5>
                        </Row>
                        <Row className="justify-content-center">
                            <img src={Six} alt="languages" />
                            <img src={Seven} alt="languages" />
                            <img src={Eight} alt="languages" />
                        </Row>
                        <Row className="justify-content-center">
                            <h5>Database & OS</h5>
                        </Row>
                        <Row className="justify-content-center">
                            <img src={One} alt="languages" /> 
                            <img src={One} alt="languages" />
                            <img src={One} alt="languages" /> 
                            <img src={One} alt="languages" />  
                        </Row>
                        <Row className="justify-content-center">
                            <h5>Tools & Version Control</h5> 
                        </Row>
                        <Row className="justify-content-center">
                            <img src={One} alt="languages" /> 
                            <img src={One} alt="languages" />
                            <img src={One} alt="languages" />
                            <img src={One} alt="languages" />   
                        </Row>
                    </Col>
                </Fade>
            </Row>
        </React.Fragment>
    )
}
