import React from 'react'
import Row from 'react-bootstrap/Row';
import DownloadIcon from '../download.jpeg';
import DownloadGif from '../dwnld.gif';
import Container from 'react-bootstrap/Container';

export default function Download() {
    const handleClick = () => {
        document.querySelector('.downloadGif').style.display="block";
        document.querySelector('.downloadIcon').style.display="none";
        setTimeout(()=> {
            location.reload();
        },4000)
    }
    return (
        <div>
            <Container>
                <Row className="justify-content-center align-items-center">
                    <img className="downloadIcon" onClick={handleClick} src={DownloadIcon} alt="download"></img>
                    <img className="downloadGif" src={DownloadGif} alt="download"></img>
                </Row>
            </Container>
        </div>
    )    
}
