import React from 'react';
import { useState } from 'react';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import ReactCardFlip from 'react-card-flip';

export default function Portfolio() {
    const [isFlipped, setIsFlipped] = useState(false);
    const handleClick = () => {
            setIsFlipped(!isFlipped)
        }

    return (
        <React.Fragment> 
            <Navbar fixed="top" bg="dark" expand="lg" variant="white">
                <Navbar.Brand href="#">Portfolio</Navbar.Brand>
                <Navbar.Toggle aria-controls="navbarScroll" />
                <Navbar.Collapse id="navbarScroll">
                    <Nav
                    className="mr-auto my-2 my-lg-0"
                    style={{ maxHeight: '120px' }}
                    >
                        <Nav.Link className="tab active" href="">About Me</Nav.Link>
                        <Nav.Link className="tab" href="">Projects</Nav.Link>
                        <Nav.Link className="tab" href="">Language Known</Nav.Link>
                        <Nav.Link className="tab" href="">Experience Details</Nav.Link>
                        <Nav.Link className="tab" href="">Download Resume</Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>
            <ReactCardFlip isFlipped={isFlipped} flipDirection="vertical">
                <div style={{background:"red"}}>
                    This is the front of the card.
                    <button onClick={handleClick}>Click to flip</button>
                </div>

                <div style={{background:"green"}}>
                    This is the back of the card.
                    <button onClick={handleClick}>Click to flip</button>
                </div>
            </ReactCardFlip>
        </React.Fragment>
    )
}
